module top_module(
input clk,
input areset,
input load,
input ena,
input [3:0] data,
output reg [3:0] q);
always @(posedge clk or posedge
areset) begin
if (areset) begin
// Asynchronous positive edge
triggered areset
q <= 4’b0;
end else if (load) begin
// Synchronous active high signals
load
q <= data;
end else if (ena) begin
// Shift right
q <= {q[2:0], 1’b0};
end
end
endmodule
